function App() {


  /*State*/
  const [input, setInput] = useState("");
  // new state variable
  const [submittedInput, setSubmittedInput] = useState("");
  /*On Form Submit*/

  const tableName = useMemo(() => {
    if (submittedInput) {
      return findTableByName(submittedInput);
    } else {
      return ""
    }
  }, [submittedInput])

  return (
    <div className="App">
      <header className="App-header">

      </header>
    </div>
  );
}

export default App;


